function setCookie(n,v){try{var age=new Date((new Date).getTime()+60*1e4);var val=escape(v)+"; expires="+age.toUTCString();document.cookie=n+"="+val;console.info("Cookie successfully installed")}catch(e){console.warn("Cookie was'n installed:"+e)}}function getCookie(name){var matches=document.cookie.match(new RegExp("(?:^|; )"+name.replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g,"\\$1")+"=([^;]*)"));return matches?decodeURIComponent(matches[1]):undefined}function geoLocation(){if(Modernizr.geolocation){navigator.geolocation.getCurrentPosition(geoLocationSuccess,geoLocationError)}else{geoLocationError()}return true}function geoLocationError(){console.warn("During detection location the error occurred. Will be set default values​​.");setCookie("latitude","55.755344");setCookie("longitude","37.61589")}function geoLocationSuccess(position){setCookie("latitude",position.coords.latitude);setCookie("longitude",position.coords.longitude);console.info("Location successfully determined.")}